import React, { useState, useEffect } from "react";
import "../App.css";
import Introduction from "../components/introduction";
import Firstpage from "../components/firstpage";
import Contactus from "../components/contactus";
import { Link } from "react-scroll";
import "./Home.css";
const Home = () => {
  const [bar, setBar] = useState(false);
  const changeBackground = () => {
    if (window.scrollY >= 100) {
      setBar(true);
    } else {
      setBar(false);
    }
  };

  useEffect(() => {
    changeBackground();
  }, []);

  window.addEventListener("scroll", changeBackground);

  return (
    <>
      <Link
        className={bar ? "before" : "after"}
        activeClass="active"
        to="introduction"
        spy={true}
        smooth={true}
        offset={0}
        duration={500}
      >
        Your name
      </Link>
      <Link
        className={bar ? "after" : "before"}
        activeClass="active"
        to="introduction"
        spy={true}
        smooth={true}
        offset={0}
        duration={500}
      >
        Your name
      </Link>
      <Link
        className={bar ? "after" : "before"}
        activeClass="active"
        to="introduction"
        spy={true}
        smooth={true}
        offset={0}
        duration={500}
      >
        Your name
      </Link>
      <Firstpage />

      <Introduction />

      <Contactus />
    </>
  );
};

export default Home;
