import React, { useState } from "react";
import {
  //Nav,
  NavLink,
  Bars,
  NavMenu,
  NavBtn,
  NavBtnLink,
} from "./NavbarElements";
import "./Navbar.css";

const Navbar = () => {
  const [navbar, setNavbar] = useState(false);
  const [home, setHome] = useState(true);

  const changeBackground = () => {
    if (window.scrollY >= 100) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };
  window.addEventListener("scroll", changeBackground);

  return (
    <>
      {home ? (
        <div className={navbar ? "navbar active" : "navbar home"}>
          {home && !navbar ? (
            <></>
          ) : (
            <NavLink to="/" onClick={() => setHome(true)}>
              Logo
            </NavLink>
          )}
          <Bars />
          <NavMenu>
            <NavLink to="/notice" activeStyle onClick={() => setHome(false)}>
              Notice
            </NavLink>
            <NavLink to="/faq" activeStyle onClick={() => setHome(false)}>
              FAQ
            </NavLink>
            <NavLink
              to="/contact-us"
              activeStyle
              onClick={() => setHome(false)}
            >
              Contact Us
            </NavLink>
            <NavLink
              to="/application"
              activeStyle
              onClick={() => setHome(false)}
            >
              Application
            </NavLink>
            <NavBtnLink className="signin" to="/sign-in">
              Sign In
            </NavBtnLink>
          </NavMenu>
        </div>
      ) : (
        <div className="navbar">
          {home && !navbar ? (
            <></>
          ) : (
            <NavLink to="/" onClick={() => setHome(true)}>
              Logo
            </NavLink>
          )}
          <Bars />
          <NavMenu>
            <NavLink to="/notice" activeStyle onClick={() => setHome(false)}>
              Notice
            </NavLink>
            <NavLink to="/faq" activeStyle onClick={() => setHome(false)}>
              FAQ
            </NavLink>
            <NavLink
              to="/contact-us"
              activeStyle
              onClick={() => setHome(false)}
            >
              Contact Us
            </NavLink>
            <NavLink
              to="/application"
              activeStyle
              onClick={() => setHome(false)}
            >
              Application
            </NavLink>
            <NavBtnLink to="/sign-in">Sign In</NavBtnLink>
          </NavMenu>
        </div>
      )}
    </>
  );
};

export default Navbar;
