import React, { useState } from "react";
import {
  //Nav,
  NavLink,
  Bars,
  NavMenu,
  NavBtn,
  NavBtnLink,
} from "./NavbarElements";
import "./Navbar.css";

const Navbar = () => {
  const [navbar, setNavbar] = useState(false);
  const [home, setHome] = useState(true);

  const changeBackground = () => {
    if (window.scrollY >= 100) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };
  window.addEventListener("scroll", changeBackground);

  return (
    <>
      <div className={home && navbar ? "navbar active" : "navbar"}>
        <NavLink to="/" onClick={setHome(true)}>
          Logo
        </NavLink>
        <Bars />
        <NavMenu>
          <NavLink to="/notice" activeStyle onClick={setHome(true)}>
            Notice
          </NavLink>
          <NavLink to="/faq" activeStyle>
            FAQ
          </NavLink>
          <NavLink to="/contact-us" activeStyle onClick={setHome(false)}>
            Contact Us
          </NavLink>
          <NavLink to="/application" activeStyle onClick={setHome(false)}>
            Application
          </NavLink>
          <NavBtnLink to="/sign-in">Sign In</NavBtnLink>
        </NavMenu>
      </div>
    </>
  );
};

export default Navbar;
